public async Task ProcessTextFileAsync(string inputPath, string outputPath, IProgress<int> progress = null)
{
    try
    {
        // Read file content
        string content = await File.ReadAllTextAsync(inputPath);

        if (string.IsNullOrEmpty(content))
        {
            progress?.Report(100);
            await File.WriteAllTextAsync(outputPath, string.Empty);
            return;
        }

        // Process content in chunks for progress reporting
        var lines = content.Split('\n');
        var processedLines = new List<string>();

        for (int i = 0; i < lines.Length; i++)
        {
            // Convert to uppercase
            processedLines.Add(lines[i].ToUpper());

            // Report progress
            int progressPercent = (int)((i + 1) * 100.0 / lines.Length);
            progress?.Report(progressPercent);

            // Small delay to show progress (optional)
            await Task.Delay(1);
        }

        // Write processed content
        string processedContent = string.Join('\n', processedLines);
        await File.WriteAllTextAsync(outputPath, processedContent);
    }
    catch (FileNotFoundException)
    {
        throw new ArgumentException($"Input file not found: {inputPath}");
    }
    catch (UnauthorizedAccessException)
    {
        throw new InvalidOperationException($"Access denied to file: {inputPath} or {outputPath}");
    }
    catch (Exception ex)
    {
        throw new InvalidOperationException($"Error processing file: {ex.Message}", ex);
    }
}
```

