public async Task<Dictionary<string, string>> GetFastestResponsesAsync(
    string[] urls, 
    int maxResults, 
    TimeSpan timeout, 
    CancellationToken cancellationToken = default)
{
    if (urls == null || urls.Length == 0)
        throw new ArgumentException("URLs array cannot be null or empty");

    if (maxResults <= 0 || maxResults > urls.Length)
        throw new ArgumentException("maxResults must be between 1 and urls.Length");

    var results = new Dictionary<string, string>();
    var completedTasks = new List<Task<(string Url, string Content, bool Success)>>();

    // Create timeout cancellation token
    using var timeoutCts = CancellationTokenSource.CreateLinkedTokenSource(cancellationToken);
    timeoutCts.CancelAfter(timeout);

    // Start all HTTP requests
    var tasks = urls.Select(url => FetchUrlAsync(url, timeoutCts.Token)).ToArray();

    try
    {
        // Wait for fastest responses
        while (results.Count < maxResults && completedTasks.Count < urls.Length)
        {
            var remainingTasks = tasks.Where(t => !completedTasks.Contains(t)).ToArray();
            if (remainingTasks.Length == 0) break;

            var completedTask = await Task.WhenAny(remainingTasks);
            completedTasks.Add(completedTask);

            var result = await completedTask;
            if (result.Success)
            {
                results[result.Url] = result.Content;
            }
        }
    }
    catch (OperationCanceledException)
    {
        // Return whatever results we have so far
    }

    return results;
}

private async Task<(string Url, string Content, bool Success)> FetchUrlAsync(string url, CancellationToken cancellationToken)
{
    try
    {
        using var client = new HttpClient();
        var response = await client.GetAsync(url, cancellationToken);
        response.EnsureSuccessStatusCode();
        var content = await response.Content.ReadAsStringAsync(cancellationToken);
        return (url, content, true);
    }
    catch
    {
        return (url, string.Empty, false);
    }
}
```
