using System;
using System.Threading;
using System.Threading.Tasks;

class Program
{
    private static readonly ReaderWriterLockSlim rwLock = new ReaderWriterLockSlim();

    static void Main()
    {
        Task.Run(() => ReadMethod("Reader1"));
        Task.Run(() => ReadMethod("Reader2"));
        Task.Run(() => WriteMethod("Writer1"));

        Thread.Sleep(2000);
    }

    static void ReadMethod(string name)
    {
        rwLock.EnterReadLock();
        try
        {
            Console.WriteLine($"{name} reading");
            Thread.Sleep(500);
        }
        finally
        {
            rwLock.ExitReadLock();
        }
    }

    static void WriteMethod(string name)
    {
        rwLock.EnterWriteLock();
        try
        {
            Console.WriteLine($"{name} writing");
            Thread.Sleep(500);
        }
        finally
        {
            rwLock.ExitWriteLock();
        }
    }
}
