```csharp
public class ThreadSafeCache<TKey, TValue>
{
    private readonly Dictionary<TKey, CacheItem<TValue>> _cache;
    private readonly ReaderWriterLockSlim _lock;
    private readonly int _maxSize;
    private readonly TimeSpan _defaultTtl;
    private readonly Timer _cleanupTimer;

    public ThreadSafeCache(int maxSize = 1000, TimeSpan? ttl = null)
    {
        _cache = new Dictionary<TKey, CacheItem<TValue>>();
        _lock = new ReaderWriterLockSlim();
        _maxSize = maxSize;
        _defaultTtl = ttl ?? TimeSpan.FromMinutes(30);

        // Cleanup expired items every minute
        _cleanupTimer = new Timer(CleanupExpiredItems, null, TimeSpan.FromMinutes(1), TimeSpan.FromMinutes(1));
    }

    public bool TryGet(TKey key, out TValue value)
    {
        _lock.EnterReadLock();
        try
        {
            if (_cache.TryGetValue(key, out var item) && !item.IsExpired)
            {
                item.LastAccessed = DateTime.UtcNow;
                value = item.Value;
                return true;
            }

            value = default(TValue);
            return false;
        }
        finally
        {
            _lock.ExitReadLock();
        }
    }

    public void Set(TKey key, TValue value, TimeSpan? ttl = null)
    {
        var expiry = DateTime.UtcNow.Add(ttl ?? _defaultTtl);
        var item = new CacheItem<TValue>(value, expiry);

        _lock.EnterWriteLock();
        try
        {
            // Remove oldest items if cache is full
            if (_cache.Count >= _maxSize && !_cache.ContainsKey(key))
            {
                EvictOldestItem();
            }

            _cache[key] = item;
        }
        finally
        {
            _lock.ExitWriteLock();
        }
    }

    public TValue GetOrAdd(TKey key, Func<TKey, TValue> valueFactory, TimeSpan? ttl = null)
    {
        // First try to get existing value
        if (TryGet(key, out TValue existingValue))
        {
            return existingValue;
        }

        // Need to create new value
        _lock.EnterWriteLock();
        try
        {
            // Double-check after acquiring write lock
            if (_cache.TryGetValue(key, out var item) && !item.IsExpired)
            {
                item.LastAccessed = DateTime.UtcNow;
                return item.Value;
            }

            // Create new value
            var newValue = valueFactory(key);
            var expiry = DateTime.UtcNow.Add(ttl ?? _defaultTtl);
            var newItem = new CacheItem<TValue>(newValue, expiry);

            // Remove oldest items if cache is full
            if (_cache.Count >= _maxSize)
            {
                EvictOldestItem();
            }

            _cache[key] = newItem;
            return newValue;
        }
        finally
        {
            _lock.ExitWriteLock();
        }
    }

    public bool Remove(TKey key)
    {
        _lock.EnterWriteLock();
        try
        {
            return _cache.Remove(key);
        }
        finally
        {
            _lock.ExitWriteLock();
        }
    }

    private void EvictOldestItem()
    {
        if (_cache.Count == 0) return;

        var oldestKey = _cache
            .OrderBy(kvp => kvp.Value.LastAccessed)
            .First().Key;

        _cache.Remove(oldestKey);
    }

    private void CleanupExpiredItems(object state)
    {
        _lock.EnterWriteLock();
        try
        {
            var expiredKeys = _cache
                .Where(kvp => kvp.Value.IsExpired)
                .Select(kvp => kvp.Key)
                .ToList();

            foreach (var key in expiredKeys)
            {
                _cache.Remove(key);
            }
        }
        finally
        {
            _lock.ExitWriteLock();
        }
    }

    public int Count
    {
        get
        {
            _lock.EnterReadLock();
            try
            {
                return _cache.Count;
            }
            finally
            {
                _lock.ExitReadLock();
            }
        }
    }

    public void Dispose()
    {
        _cleanupTimer?.Dispose();
        _lock?.Dispose();
    }

    private class CacheItem<T>
    {
        public T Value { get; }
        public DateTime ExpiryTime { get; }
        public DateTime LastAccessed { get; set; }
        public bool IsExpired => DateTime.UtcNow > ExpiryTime;

        public CacheItem(T value, DateTime expiryTime)
        {
            Value = value;
            ExpiryTime = expiryTime;
            LastAccessed = DateTime.UtcNow;
        }
    }
}
```

