// Usage example:
var connectionPool = new ResourcePool<DatabaseConnection>(
    maxSize: 10,
    factory: () => new DatabaseConnection(connectionString),
    validator: conn => conn.IsConnected,
    cleanup: conn => conn.Dispose()
);

// Multiple threads acquiring resources
var tasks = new List<Task>();

for (int i = 0; i < 20; i++)
{
    int threadId = i;
    tasks.Add(Task.Run(async () =>
    {
        try
        {
            using (var resource = await connectionPool.AcquireAsync(
                timeout: TimeSpan.FromSeconds(30)))
            {
                var connection = resource.Value;
                Console.WriteLine($"Thread {threadId}: Got connection {connection.Id}");

                // Simulate work
                await connection.ExecuteQueryAsync("SELECT COUNT(*) FROM Users");
                await Task.Delay(1000);

                Console.WriteLine($"Thread {threadId}: Releasing connection");
            } // Resource automatically returned here
        }
        catch (TimeoutException)
        {
            Console.WriteLine($"Thread {threadId}: Timeout waiting for resource");
        }
    }));
}

await Task.WhenAll(tasks);
Console.WriteLine($"Available: {connectionPool.AvailableCount}, Total: {connectionPool.TotalCount}");
```

