```csharp
public class ResourcePool<T> : IDisposable where T : class
{
    private readonly ConcurrentQueue<T> _resources;
    private readonly SemaphoreSlim _semaphore;
    private readonly Func<T> _factory;
    private readonly Func<T, bool> _validator;
    private readonly Action<T> _cleanup;
    private readonly int _maxSize;
    private volatile bool _disposed;
    private readonly object _lockObject = new object();
    private int _currentSize;

    public ResourcePool(
        int maxSize,
        Func<T> factory,
        Func<T, bool> validator = null,
        Action<T> cleanup = null)
    {
        _maxSize = maxSize;
        _factory = factory ?? throw new ArgumentNullException(nameof(factory));
        _validator = validator ?? (resource => true);
        _cleanup = cleanup ?? (resource => { });

        _resources = new ConcurrentQueue<T>();
        _semaphore = new SemaphoreSlim(maxSize, maxSize);
        _currentSize = 0;
    }

    public async Task<PooledResource<T>> AcquireAsync(TimeSpan timeout = default, CancellationToken cancellationToken = default)
    {
        if (_disposed)
            throw new ObjectDisposedException(nameof(ResourcePool<T>));

        var actualTimeout = timeout == default ? TimeSpan.FromSeconds(30) : timeout;

        if (!await _semaphore.WaitAsync(actualTimeout, cancellationToken))
        {
            throw new TimeoutException($"Failed to acquire resource within {actualTimeout}");
        }

        try
        {
            T resource = null;

            // Try to get existing resource from pool
            while (_resources.TryDequeue(out resource))
            {
                if (_validator(resource))
                {
                    return new PooledResource<T>(resource, this);
                }
                else
                {
                    // Resource is invalid, clean it up
                    _cleanup(resource);
                    lock (_lockObject)
                    {
                        _currentSize--;
                    }
                }
            }

            // Create new resource if needed
            lock (_lockObject)
            {
                if (_currentSize < _maxSize)
                {
                    resource = _factory();
                    _currentSize++;
                    return new PooledResource<T>(resource, this);
                }
            }

            // This shouldn't happen due to semaphore, but handle it gracefully
            throw new InvalidOperationException("Unable to create or acquire resource");
        }
        catch
        {
            _semaphore.Release();
            throw;
        }
    }

    public PooledResource<T> Acquire(TimeSpan timeout = default)
    {
        return AcquireAsync(timeout).GetAwaiter().GetResult();
    }

    internal void Return(T resource)
    {
        if (_disposed)
        {
            _cleanup(resource);
            return;
        }

        try
        {
            if (_validator(resource))
            {
                _resources.Enqueue(resource);
            }
            else
            {
                _cleanup(resource);
                lock (_lockObject)
                {
                    _currentSize--;
                }
            }
        }
        finally
        {
            _semaphore.Release();
        }
    }

    public int AvailableCount => _semaphore.CurrentCount;
    public int TotalCount => _currentSize;

    public void Dispose()
    {
        if (_disposed) return;

        _disposed = true;

        // Clean up all resources
        while (_resources.TryDequeue(out T resource))
        {
            _cleanup(resource);
        }

        _semaphore?.Dispose();
    }
}

public class PooledResource<T> : IDisposable where T : class
{
    private readonly ResourcePool<T> _pool;
    private T _resource;
    private bool _disposed;

    internal PooledResource(T resource, ResourcePool<T> pool)
    {
        _resource = resource;
        _pool = pool;
    }

    public T Value
    {
        get
        {
            if (_disposed)
                throw new ObjectDisposedException(nameof(PooledResource<T>));
            return _resource;
        }
    }

    public void Dispose()
    {
        if (_disposed) return;

        _disposed = true;

        if (_resource != null)
        {
            _pool.Return(_resource);
            _resource = null;
        }
    }
}
```
